import Axios from 'axios';
import PdfDocument  from 'pdfkit';
import { Order } from '../entity';

export class OrderPdfTemplate {
    private static defaultIndent = 40;
    private static defaultFontSize = 12;

    private readonly doc = new PdfDocument({ size: 'A4', margin: OrderPdfTemplate.defaultIndent });

    private static fontBuffer?: [Buffer, Buffer];

    constructor(private readonly order: Order) {}

    public async convert(): Promise<PDFKit.PDFDocument> {
        const [r, b] = await OrderPdfTemplate.getFont();
        this.doc.registerFont("PT Sans", r, 'PT Sans');
        this.doc.registerFont("PT Sans Bold", b, 'PT Sans Bold');
        this.doc.font(r);
        this.doc.fontSize(OrderPdfTemplate.defaultFontSize);

        this.doc.image(base64, OrderPdfTemplate.getIndent(0), OrderPdfTemplate.getIndent(35), { width: 150 });

        await this.loadHeader();
        await this.loadContent();
        await this.loadExtraAdv();

        return this.doc;
    }

    private async loadHeader(): Promise<this> {
        const [r, b] = await OrderPdfTemplate.getFont();
        const w = 300;
        const textOpts = {
            width: 300,
        };
        const lineSpace = .3;

        this.doc.font(b)
            .text('ООО «Казанский технадзор»', w, OrderPdfTemplate.getIndent(35), textOpts)
            .moveDown(lineSpace)
            .font(r)
            .text('ОГРН 1181690085170, ИНН 1657249613/ КПП 165701001', w, undefined, textOpts)
            .moveDown(lineSpace)
            .text('Юридический адрес: 421001, Республика Татарстан, г. Казань, ул. Чистопольская д. 79, офис 18', w, undefined, textOpts)
            .moveDown(lineSpace)
            .text('Телефон +7(843)253-77-77 +7 (903) 388-77-77', w, undefined, textOpts)
            .moveDown(lineSpace)
            .text('e-mail: kzn-tehnadzor@yandex.ru', w, undefined, textOpts)
            .moveDown(lineSpace)
        return this;
    }

    private async loadContent(): Promise<this> {
        const [r, b] = await OrderPdfTemplate.getFont();
        this.doc.font(b)
            .fontSize(20)
            .text(`Сертификат №${this.order.id}`, OrderPdfTemplate.defaultIndent, OrderPdfTemplate.getIndent(220), { align: 'center' })
            .moveDown()
            .font(r)
            .fontSize(OrderPdfTemplate.defaultFontSize)
            .text(`На осмотр ${this.order.buildType === 'house' ? 'готового дома' : 'готовой квартиры'} перед покупкой, площадью ${this.order.area}м². Владелец ${this.order.fullName} стоимостью ${this.order.price.toLocaleString('ru')} ₽`)
            .moveDown()
            .font(b)
            .text(`Дата покупки сертификата ${this.order.createdAt.toLocaleDateString('ru', { timeZone: 'Europe/Moscow' })}`, { align: 'right' })
            .moveDown()
            .font(r);

        return this;
    }

    private async loadExtraAdv(): Promise<this> {
        const [r, b] = await OrderPdfTemplate.getFont();
        const lineSpace = .3;

        this.doc.moveDown()
            .moveTo(OrderPdfTemplate.defaultIndent, this.doc.y)
            .lineTo(this.doc.page.width - OrderPdfTemplate.defaultIndent, this.doc.y)
            .stroke()
            .moveDown()
            .fontSize(8)
            .text('Казанский технадзор оказывает более 10 различных услуг, связанных с техническим надзором за строительством, приемом готовых объектов, а также прием отдельных видов строительно-технических работ (электроизмерения).')
            .moveDown(lineSpace)
            .text('Также мы оказываем услуги по многим сопутствующим строительству вопросам от подбора участка и выбора подрядчика, юридическому сопровождению строительства до контроля качества используемых строительных материалов и консультаций по строительно-техническим вопросам. Дополнительно осуществляем аудит проектной и сметной документации, выполняем инженерно-геологические и геодезические изыскания.')
            .moveDown(lineSpace)
            .font(b)
            .text('Перечень сертификатов и описание услуг:')
            .font(r)
            .moveDown(lineSpace)
            .list([
                'Осмотр готового дома перед покупкой',
                'Техническое сопровождение при строительстве дома',
                'Услуги по электроизмерениям электроточек индивидуального жилого дома / квартиры',
                'Разовый выезд',
                'Выезд с консультацией',
                'Техническое обследование здания (фундамента)',
                'Сертификаты на сумму 1000, 2000, 5000, 15000 рублей',
                'Землеустроительные работы',
                'Инженерно-геологические изыскания',
                'Юридическое сопровождение по вопросам строительства',
                'Контроль качества используемых материалов',
                'Аудит и разработка сметной и проектной документации',
                'Проведение контрольных обмеров объёмов СМР',
                'Полное техническое сопровождение объекта с постоянным нахождением специалиста на объекте (функция технического заказчика или руководителя объекта по строительству)',
            ], OrderPdfTemplate.getIndent(50))
            .moveDown(lineSpace)
            .text('За подробной информацией обращаться по телефону +7 (843) 253-7777', OrderPdfTemplate.defaultIndent, undefined, {
                align: 'right'
            })
            

        return this;
    }

    private static async getFont(): Promise<[Buffer, Buffer]> {
        if (OrderPdfTemplate.fontBuffer) return OrderPdfTemplate.fontBuffer;
        const fonts = await Promise.all([
            Axios.get('https://www.culture.ru/frontend-next/fonts/PTSans/PTSansRegular.woff', { responseType: 'arraybuffer'}).then(r => r.data),
            Axios.get('https://www.culture.ru/frontend-next/fonts/PTSans/PTSansBold.woff', { responseType: 'arraybuffer'}).then(r => r.data),
        ]);

        return OrderPdfTemplate.fontBuffer = fonts;
    }

    private static getIndent(v: number): number {
        return v + OrderPdfTemplate.defaultIndent;
    }
}

const base64 = 'data:image/jpeg;base64,'